/* Navegación Mobile-First */
nav {
    order: 3;
    width: 100%;
    display: none;
}

nav.active {
    display: block;
}

nav ul {
    display: flex;
    flex-direction: column;
    list-style: none;
    gap: 0.5rem;
    width: 100%;
}

nav ul li a {
    display: flex;
    align-items: center;
    color: white;
    text-decoration: none;
    padding: 0.75rem 1rem;
    border-radius: 4px;
    transition: background-color 0.3s;
    width: 100%;
    font-size: 1rem;
}

nav ul li a:hover,
nav ul li a.active {
    background: #3498db;
    color: white;
}

/* Hamburger Menu */
.mobile-menu-btn {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 30px;
    height: 21px;
    background: none;
    border: none;
    cursor: pointer;
    padding: 0;
}

.mobile-menu-btn span {
    display: block;
    height: 3px;
    width: 100%;
    background-color: white;
    border-radius: 3px;
    transition: all 0.3s ease;
}

.mobile-menu-btn.active span:nth-child(1) {
    transform: rotate(45deg) translate(6px, 6px);
}

.mobile-menu-btn.active span:nth-child(2) {
    opacity: 0;
}

.mobile-menu-btn.active span:nth-child(3) {
    transform: rotate(-45deg) translate(6px, -6px);
}

.cart-icon {
    position: relative;
    cursor: pointer;
    font-size: 1.5rem;
    padding: 0.5rem;
    transition: transform 0.3s;
    display: flex;
    align-items: center;
}

.cart-icon:hover {
    transform: scale(1.1);
}

.cart-count {
    background: #e74c3c;
    color: white;
    border-radius: 50%;
    padding: 0.2rem 0.5rem;
    font-size: 0.8rem;
    position: absolute;
    top: -5px;
    right: -5px;
    min-width: 20px;
    text-align: center;
}

/* Main content */
main {
    min-height: calc(100vh - 80px);
}

/* Products Header */
.products-header {
    text-align: center;
    padding: 3rem 1rem 2rem;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    margin-bottom: 2rem;
}

.products-header h1 {
    font-size: clamp(1.8rem, 4vw, 2.5rem);
    margin-bottom: 1rem;
}

.products-header p {
    font-size: clamp(1rem, 2.5vw, 1.2rem);
    opacity: 0.9;
    max-width: 600px;
    margin: 0 auto;
    line-height: 1.4;
}

/* Products Grid */
.products {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 1.5rem;
    padding: 1rem;
    max-width: 1200px;
    margin: 0 auto;
}

.product {
    background: white;
    border: 1px solid #e1e8ed;
    border-radius: 8px;
    padding: 1rem;
    text-align: center;
    transition: transform 0.3s, box-shadow 0.3s;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

.product:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 15px rgba(0,0,0,0.2);
}

.product img {
    width: 100%;
    height: 200px;
    object-fit: cover;
    border-radius: 4px;
    margin-bottom: 1rem;
}

.product h3 {
    margin: 1rem 0;
    color: #2c3e50;
    font-size: 1.2rem;
}

.product .category {
    color: #7f8c8d;
    font-size: 0.9rem;
    text-transform: uppercase;
    margin-bottom: 0.5rem;
}

.price {
    font-weight: bold;
    color: #e44d26;
    font-size: 1.4rem;
    margin: 1rem 0;
}

.add-to-cart {
    background: #3498db;
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1rem;
    transition: background-color 0.3s;
    width: 100%;
    min-height: 44px;
}

.add-to-cart:hover {
    background: #2980b9;
}

/* Hero Carousel Responsive */
.hero-carousel {
    position: relative;
    height: 60vh;
    min-height: 400px;
    max-height: 600px;
    overflow: hidden;
    border-radius: 0 0 20px 20px;
}

.carousel-container {
    position: relative;
    height: 100%;
    width: 100%;
}

.carousel-slide {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
    display: flex;
    align-items: center;
    justify-content: center;
}

.carousel-slide.active {
    opacity: 1;
}

.carousel-slide::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.4);
}

.carousel-content {
    position: relative;
    z-index: 2;
    text-align: center;
    color: white;
    max-width: 90%;
    padding: 1rem;
}

.carousel-content h1 {
    font-size: clamp(1.8rem, 5vw, 3rem);
    margin-bottom: 1rem;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
    line-height: 1.2;
}

.carousel-content p {
    font-size: clamp(1rem, 3vw, 1.2rem);
    margin-bottom: 2rem;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
    line-height: 1.4;
}

.cta-buttons {
    display: flex;
    gap: 1rem;
    justify-content: center;
    flex-wrap: wrap;
}

.cta-button {
    background: #e74c3c;
    color: white;
    border: none;
    padding: clamp(0.75rem, 3vw, 1rem) clamp(1.5rem, 4vw, 2rem);
    font-size: clamp(0.9rem, 2.5vw, 1.1rem);
    border-radius: 50px;
    cursor: pointer;
    transition: all 0.3s ease;
    text-transform: uppercase;
    font-weight: bold;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    min-height: 44px;
    min-width: 44px;
}

.cta-button:hover {
    background: #c0392b;
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
}

.cta-button.primary {
    background: #e74c3c;
}

.cta-button.secondary {
    background: transparent;
    color: white;
    border: 2px solid white;
}

.cta-button.secondary:hover {
    background: white;
    color: #333;
}

.carousel-btn {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background: rgba(255, 255, 255, 0.8);
    border: none;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 3;
    transition: background 0.3s ease;
    min-height: 44px;
    min-width: 44px;
}

.carousel-btn:hover {
    background: rgba(255, 255, 255, 1);
}

.carousel-btn.prev {
    left: 1rem;
}

.carousel-btn.next {
    right: 1rem;
}

.carousel-dots {
    position: absolute;
    bottom: 1rem;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 0.5rem;
    z-index: 3;
}

.dot {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    border: none;
    background: rgba(255, 255, 255, 0.5);
    cursor: pointer;
    transition: background 0.3s ease;
    min-height: 12px;
    min-width: 12px;
}

.dot.active {
    background: white;
}

/* Categories Section Responsive */
.categories-section {
    padding: 3rem 1rem;
    background: #f8f9fa;
}

.categories-section h2 {
    text-align: center;
    margin-bottom: 1rem;
    font-size: clamp(1.8rem, 4vw, 2.5rem);
    color: #2c3e50;
}

.section-subtitle {
    text-align: center;
    font-size: clamp(1rem, 2.5vw, 1.2rem);
    color: #7f8c8d;
    margin-bottom: 2rem;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
    line-height: 1.4;
}

.categories-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 1.5rem;
    max-width: 1000px;
    margin: 0 auto;
}

.category-card {
    background: white;
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
    cursor: pointer;
}

.category-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

.category-image {
    position: relative;
    height: 200px;
    overflow: hidden;
}

.category-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.category-card:hover .category-image img {
    transform: scale(1.1);
}

.category-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(52, 152, 219, 0.9);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.category-card:hover .category-overlay {
    opacity: 1;
}

.category-icon {
    color: white;
}

.category-content {
    padding: 1.5rem;
    text-align: center;
}

.category-content h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: #2c3e50;
}

.category-content p {
    color: #7f8c8d;
    margin-bottom: 1.5rem;
    line-height: 1.6;
}

.category-btn {
    background: #3498db;
    color: white;
    border: none;
    padding: 0.75rem 2rem;
    border-radius: 25px;
    cursor: pointer;
    transition: background 0.3s ease;
    font-weight: bold;
    min-height: 44px;
}

.category-btn:hover {
    background: #2980b9;
}

.view-all-categories {
    text-align: center;
    margin-top: 2rem;
}

.view-all-btn {
    background: #3498db;
    color: white;
    border: none;
    padding: 1rem 2rem;
    border-radius: 5px;
    cursor: pointer;
    transition: background 0.3s ease;
    display: inline-flex;
    align-items: center;
    font-size: 1rem;
    min-height: 44px;
}

.view-all-btn:hover {
    background: #2980b9;
    transform: translateY(-2px);
}

/* Features Section Responsive */
.features-section {
    padding: 3rem 1rem;
}

.features-section h2 {
    text-align: center;
    margin-bottom: 1rem;
    font-size: clamp(1.8rem, 4vw, 2.5rem);
    color: #2c3e50;
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
    max-width: 1000px;
    margin: 0 auto;
}

.feature-card {
    text-align: center;
    padding: 1.5rem;
    background: white;
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    transition: transform 0.3s ease;
}

.feature-card:hover {
    transform: translateY(-5px);
}

.feature-icon {
    color: #3498db;
    margin-bottom: 1rem;
}

.feature-card h3 {
    margin-bottom: 1rem;
    color: #2c3e50;
}

.feature-card p {
    color: #7f8c8d;
    line-height: 1.6;
}

/* About Section Responsive */
.about-section {
    padding: 3rem 1rem;
    background: #2c3e50;
    color: white;
}

.about-content {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}

.about-text h2 {
    font-size: clamp(1.8rem, 4vw, 2.5rem);
    margin-bottom: 1.5rem;
    color: white;
}

.about-text p {
    margin-bottom: 1.5rem;
    font-size: 1rem;
    line-height: 1.6;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 1rem;
    margin-top: 2rem;
}

.stat {
    text-align: center;
    padding: 1rem;
}

.stat h3 {
    font-size: clamp(1.5rem, 3vw, 2.5rem);
    margin-bottom: 0.5rem;
    color: #3498db;
}

.stat p {
    margin: 0;
    font-size: 0.9rem;
    opacity: 0.9;
}

.about-image img {
    width: 100%;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

/* CTA Section Responsive */
.cta-section {
    padding: 3rem 1rem;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    text-align: center;
}

.cta-content h2 {
    font-size: clamp(1.8rem, 4vw, 3rem);
    margin-bottom: 1rem;
}

.cta-content p {
    font-size: clamp(1rem, 2.5vw, 1.3rem);
    margin-bottom: 2rem;
    opacity: 0.9;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
    line-height: 1.4;
}

.cta-section .cta-buttons {
    display: flex;
    gap: 1rem;
    justify-content: center;
    flex-wrap: wrap;
}

.cta-section .cta-button.primary {
    background: white;
    color: #667eea;
}

.cta-section .cta-button.secondary {
    background: transparent;
    color: white;
    border: 2px solid white;
}

.cta-section .cta-button.secondary:hover {
    background: white;
    color: #667eea;
}

/* Testimonials Section Responsive */
.testimonials-section {
    padding: 3rem 1rem;
    background: #f8f9fa;
}

.testimonials-section h2 {
    text-align: center;
    margin-bottom: 1rem;
    font-size: clamp(1.8rem, 4vw, 2.5rem);
    color: #2c3e50;
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 1.5rem;
    max-width: 1000px;
    margin: 0 auto;
}

.testimonial-card {
    background: white;
    padding: 1.5rem;
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    text-align: center;
}

.stars {
    margin-bottom: 1rem;
}

.testimonial-card p {
    font-style: italic;
    margin-bottom: 1.5rem;
    line-height: 1.6;
}

.client-info {
    border-top: 1px solid #eee;
    padding-top: 1rem;
}

.client-info strong {
    display: block;
    margin-bottom: 0.5rem;
    color: #2c3e50;
}

.client-info span {
    color: #7f8c8d;
    font-size: 0.9rem;
}

/* Cart Responsive */
.cart-sidebar {
    position: fixed;
    right: -100%;
    top: 0;
    width: 100%;
    height: 100vh;
    background: white;
    box-shadow: -2px 0 10px rgba(0,0,0,0.1);
    transition: right 0.3s ease;
    z-index: 1000;
    display: flex;
    flex-direction: column;
}

.cart-sidebar.active {
    right: 0;
}

.cart-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0,0,0,0.5);
    z-index: 999;
    display: none;
}

.cart-overlay.active {
    display: block;
}

.cart-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem;
    border-bottom: 1px solid #eee;
}

.cart-header h2 {
    margin: 0;
    color: #2c3e50;
}

.close-cart {
    background: none;
    border: none;
    font-size: 2rem;
    cursor: pointer;
    color: #7f8c8d;
    min-height: 44px;
    min-width: 44px;
}

.close-cart:hover {
    color: #e74c3c;
}

.cart-items {
    flex: 1;
    padding: 1rem;
    overflow-y: auto;
}

.cart-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem;
    border-bottom: 1px solid #eee;
    flex-wrap: wrap;
    gap: 0.5rem;
}

.cart-item-info h4 {
    margin: 0;
    color: #2c3e50;
    font-size: 1rem;
}

.cart-item-info p {
    margin: 0.25rem 0;
    color: #7f8c8d;
    font-size: 0.9rem;
}

.cart-item-controls {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.quantity-btn {
    background: #bdc3c7;
    border: none;
    width: 30px;
    height: 30px;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 44px;
    min-width: 44px;
}

.quantity-btn:hover {
    background: #95a5a6;
}

.remove-btn {
    background: #e74c3c;
    color: white;
    border: none;
    padding: 0.5rem 1rem;
    border-radius: 3px;
    cursor: pointer;
    margin-left: 0.5rem;
    min-height: 44px;
}

.remove-btn:hover {
    background: #c0392b;
}

.cart-total {
    padding: 1.5rem;
    border-top: 1px solid #eee;
    background: #f8f9fa;
}

.cart-total h3 {
    margin: 0;
    color: #2c3e50;
    font-size: 1.5rem;
}

.checkout-btn {
    background: #27ae60;
    color: white;
    border: none;
    padding: 1rem;
    font-size: 1.1rem;
    cursor: pointer;
    transition: background-color 0.3s;
    margin: 0 1rem 1rem;
    min-height: 44px;
}

.checkout-btn:hover {
    background: #219a52;
}

.clear-cart-btn {
    background: #e74c3c;
    color: white;
    border: none;
    padding: 0.75rem;
    cursor: pointer;
    transition: background-color 0.3s;
    margin: 0 1rem 1.5rem;
    min-height: 44px;
}

.clear-cart-btn:hover {
    background: #c0392b;
}

.empty-cart {
    text-align: center;
    color: #7f8c8d;
    padding: 2rem;
}

/* Container utility */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
}

/* Media Queries para tablets y desktop */
@media (min-width: 768px) {
    body {
        font-size: 18px;
    }
    
    header {
        flex-wrap: nowrap;
        padding: 1rem 2rem;
    }
    
    nav {
        order: 2;
        width: auto;
        display: block !important;
    }
    
    nav ul {
        flex-direction: row;
        gap: 1rem;
        width: auto;
    }
    
    nav ul li a {
        width: auto;
        padding: 0.5rem 1rem;
    }
    
    .mobile-menu-btn {
        display: none;
    }
    
    .hero-carousel {
        height: 70vh;
    }
    
    .carousel-content {
        max-width: 600px;
        padding: 0 2rem;
    }
    
    .about-content {
        grid-template-columns: 2fr 1fr;
        gap: 4rem;
    }
    
    .cart-sidebar {
        width: 400px;
        right: -400px;
    }
    
    .categories-section,
    .features-section,
    .about-section,
    .testimonials-section {
        padding: 4rem 2rem;
    }
    
    .categories-grid,
    .features-grid {
        gap: 2rem;
    }
    
    .cart-item {
        flex-wrap: nowrap;
    }
}

@media (min-width: 1024px) {
    .hero-carousel {
        height: 80vh;
        max-height: 700px;
    }
    
    .carousel-content h1 {
        font-size: 3.5rem;
    }
    
    .carousel-content p {
        font-size: 1.4rem;
    }
}

/* Mejoras específicas para móviles muy pequeños */
@media (max-width: 360px) {
    .logo {
        font-size: 1.2rem;
    }
    
    .cta-buttons {
        flex-direction: column;
        align-items: center;
    }
    
    .cta-button {
        width: 100%;
        max-width: 250px;
    }
    
    .categories-grid,
    .products {
        grid-template-columns: 1fr;
    }
    
    .product {
        padding: 0.75rem;
    }
    
    .category-content {
        padding: 1rem;
    }
}

/* Prevenir zoom en inputs en iOS */
@media (max-width: 768px) {
    input, select, textarea {
        font-size: 16px;
    }
}

/* Mejorar scroll en móviles */
@media (max-width: 768px) {
    html {
        -webkit-overflow-scrolling: touch;
    }
    
    body {
        -webkit-tap-highlight-color: transparent;
    }
}

/* Product Image Container */
.product-image-container {
    position: relative;
    width: 100%;
    height: 200px;
    margin-bottom: 1rem;
}

.product-image-container img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 4px;
}

.product-image-placeholder {
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 4px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-weight: bold;
    text-align: center;
    padding: 1rem;
}

/* Colores diferentes por categoría */
.product[data-category="regalos"] .product-image-placeholder {
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
}

.product[data-category="ropa"] .product-image-placeholder {
    background: linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);
}

.product[data-category="servicios"] .product-image-placeholder {
    background: linear-gradient(135deg, #43e97b 0%, #38f9d7 100%);
}

/* Auth Styles */
.header-actions {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.login-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: transparent;
    color: white;
    border: 1px solid rgba(255, 255, 255, 0.3);
    padding: 0.5rem 1rem;
    border-radius: 5px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.9rem;
}

.login-btn:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.5);
}

.user-menu {
    position: relative;
}

.user-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: transparent;
    color: white;
    border: 1px solid rgba(255, 255, 255, 0.3);
    padding: 0.5rem 1rem;
    border-radius: 5px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.9rem;
}

.user-btn:hover {
    background: rgba(255, 255, 255, 0.1);
}

.user-name {
    font-weight: 500;
}

.user-dropdown {
    position: absolute;
    top: 100%;
    right: 0;
    margin-top: 0.5rem;
    background: white;
    border-radius: 8px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
    min-width: 200px;
    z-index: 1000;
    overflow: hidden;
}

.user-info {
    padding: 1rem;
    border-bottom: 1px solid #eee;
    background: #f8f9fa;
}

.user-info strong {
    display: block;
    color: #2c3e50;
    margin-bottom: 0.25rem;
}

.user-info span {
    font-size: 0.8rem;
    color: #7f8c8d;
}

.user-actions {
    padding: 0.5rem 0;
}

.user-action-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    width: 100%;
    padding: 0.75rem 1rem;
    background: none;
    border: none;
    text-align: left;
    cursor: pointer;
    transition: background 0.3s ease;
    font-size: 0.9rem;
    color: #2c3e50;
}

.user-action-btn:hover {
    background: #f8f9fa;
}

.user-action-btn.logout {
    color: #e74c3c;
    border-top: 1px solid #eee;
    margin-top: 0.5rem;
}

/* Modal Styles */
.modal-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 2000;
    padding: 1rem;
}

.modal-overlay.active {
    display: flex;
}

.modal-content {
    background: white;
    border-radius: 15px;
    padding: 0;
    max-width: 450px;
    width: 100%;
    max-height: 90vh;
    overflow-y: auto;
    position: relative;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
}

.modal-close {
    position: absolute;
    top: 1rem;
    right: 1rem;
    background: none;
    border: none;
    cursor: pointer;
    color: #7f8c8d;
    z-index: 1;
    padding: 0.5rem;
    border-radius: 50%;
    transition: background 0.3s ease;
}

.modal-close:hover {
    background: #f8f9fa;
}

/* Auth Modal Styles */
.auth-modal {
    padding: 2rem;
}

.auth-header {
    text-align: center;
    margin-bottom: 2rem;
}

.auth-header h2 {
    color: #2c3e50;
    margin-bottom: 0.5rem;
    font-size: 1.8rem;
}

.auth-header p {
    color: #7f8c8d;
    font-size: 1rem;
}

.auth-form {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.form-group {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
}

.form-group label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-weight: 500;
    color: #2c3e50;
    font-size: 0.9rem;
}

.form-group input {
    padding: 0.75rem 1rem;
    border: 2px solid #e1e8ed;
    border-radius: 8px;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.form-group input:focus {
    outline: none;
    border-color: #3498db;
}

.password-input {
    position: relative;
}

.password-input input {
    width: 100%;
    padding-right: 3rem;
}

.password-toggle {
    position: absolute;
    right: 0.75rem;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    color: #7f8c8d;
    padding: 0.25rem;
}

.password-toggle:hover {
    color: #3498db;
}

.auth-button {
    padding: 1rem;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    margin-top: 0.5rem;
}

.auth-button.primary {
    background: #3498db;
    color: white;
}

.auth-button.primary:hover:not(:disabled) {
    background: #2980b9;
    transform: translateY(-2px);
}

.auth-button:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none !important;
}

.auth-error {
    background: #fee;
    color: #c0392b;
    padding: 0.75rem 1rem;
    border-radius: 8px;
    border: 1px solid #f5b7b1;
    font-size: 0.9rem;
}

.auth-footer {
    text-align: center;
    margin-top: 1.5rem;
    padding-top: 1.5rem;
    border-top: 1px solid #e1e8ed;
}

.auth-footer p {
    color: #7f8c8d;
    font-size: 0.9rem;
}

.auth-link {
    background: none;
    border: none;
    color: #3498db;
    cursor: pointer;
    text-decoration: underline;
    font-size: inherit;
}

.auth-link:hover {
    color: #2980b9;
}

/* Responsive Auth */
@media (max-width: 768px) {
    .header-actions {
        gap: 0.5rem;
    }
    
    .login-btn span,
    .user-name {
        display: none;
    }
    
    .login-btn,
    .user-btn {
        padding: 0.5rem;
    }
    
    .auth-modal {
        padding: 1.5rem;
    }
    
    .modal-content {
        margin: 1rem;
    }
}

@media (max-width: 480px) {
    .auth-modal {
        padding: 1rem;
    }
    
    .auth-header h2 {
        font-size: 1.5rem;
    }
}

/* Profile Page Styles */
.profile-page {
    min-height: 100vh;
    background: #f8f9fa;
    padding: 2rem 0;
}

.profile-header {
    background: white;
    border-radius: 15px;
    padding: 2rem;
    display: flex;
    align-items: center;
    gap: 2rem;
    margin-bottom: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    position: relative;
}

.avatar-circle {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.5rem;
    font-weight: bold;
}

.profile-info h1 {
    margin: 0 0 0.5rem 0;
    color: #2c3e50;
    font-size: 1.8rem;
}

.profile-info p {
    margin: 0 0 1.5rem 0;
    color: #7f8c8d;
}

.profile-stats {
    display: flex;
    gap: 2rem;
}

.stat {
    text-align: center;
}

.stat strong {
    display: block;
    font-size: 1.5rem;
    color: #2c3e50;
    margin-bottom: 0.25rem;
}

.stat span {
    font-size: 0.9rem;
    color: #7f8c8d;
}

.edit-profile-btn {
    position: absolute;
    top: 2rem;
    right: 2rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: #3498db;
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    border-radius: 8px;
    cursor: pointer;
    transition: background 0.3s ease;
}

.edit-profile-btn:hover {
    background: #2980b9;
}

/* Profile Tabs */
.profile-tabs {
    display: flex;
    background: white;
    border-radius: 15px;
    padding: 0.5rem;
    margin-bottom: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    overflow-x: auto;
}

.tab-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: none;
    border: none;
    padding: 1rem 1.5rem;
    border-radius: 10px;
    cursor: pointer;
    transition: all 0.3s ease;
    color: #7f8c8d;
    white-space: nowrap;
    flex: 1;
    justify-content: center;
}

.tab-btn:hover {
    background: #f8f9fa;
    color: #2c3e50;
}

.tab-btn.active {
    background: #3498db;
    color: white;
}

/* Tab Content */
.tab-content {
    background: white;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

/* Profile Content */
.info-card {
    margin-bottom: 2rem;
}

.info-card h3 {
    margin: 0 0 1.5rem 0;
    color: #2c3e50;
    font-size: 1.3rem;
}

.info-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
}

.info-item label {
    display: block;
    font-weight: 500;
    color: #7f8c8d;
    margin-bottom: 0.5rem;
    font-size: 0.9rem;
}

.info-item p {
    margin: 0;
    color: #2c3e50;
    font-size: 1rem;
}

.preferences {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.preference-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
}

.preference-item input[type="checkbox"] {
    width: 18px;
    height: 18px;
}

.preference-item label {
    margin: 0;
    color: #2c3e50;
    cursor: pointer;
}

/* Address Manager */
.section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 2rem;
}

.section-header h3 {
    margin: 0;
    color: #2c3e50;
    font-size: 1.5rem;
}

.add-address-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: #27ae60;
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    border-radius: 8px;
    cursor: pointer;
    transition: background 0.3s ease;
}

.add-address-btn:hover {
    background: #219a52;
}

.addresses-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 1.5rem;
}

.address-card {
    background: #f8f9fa;
    border: 2px solid #e1e8ed;
    border-radius: 10px;
    padding: 1.5rem;
    position: relative;
    transition: all 0.3s ease;
}

.address-card.default {
    border-color: #3498db;
    background: #ebf5fb;
}

.default-badge {
    position: absolute;
    top: -10px;
    left: 1rem;
    background: #3498db;
    color: white;
    padding: 0.25rem 0.75rem;
    border-radius: 15px;
    font-size: 0.8rem;
    display: flex;
    align-items: center;
    gap: 0.25rem;
}

.address-content h4 {
    margin: 0 0 0.5rem 0;
    color: #2c3e50;
}

.address-content p {
    margin: 0.25rem 0;
    color: #7f8c8d;
    font-size: 0.9rem;
}

.address-actions {
    display: flex;
    gap: 0.5rem;
    margin-top: 1rem;
}

.action-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 36px;
    height: 36px;
    border: none;
    border-radius: 6px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.9rem;
}

.action-btn.set-default {
    background: #f8f9fa;
    color: #f39c12;
    border: 1px solid #f39c12;
}

.action-btn.set-default:hover {
    background: #f39c12;
    color: white;
}

.action-btn.edit {
    background: #f8f9fa;
    color: #3498db;
    border: 1px solid #3498db;
}

.action-btn.edit:hover {
    background: #3498db;
    color: white;
}

.action-btn.delete {
    background: #f8f9fa;
    color: #e74c3c;
    border: 1px solid #e74c3c;
}

.action-btn.delete:hover {
    background: #e74c3c;
    color: white;
}

/* Order History */
.orders-list {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.order-card {
    background: #f8f9fa;
    border-radius: 10px;
    padding: 1.5rem;
    border: 1px solid #e1e8ed;
}

.order-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 1rem;
}

.order-info h4 {
    margin: 0 0 0.5rem 0;
    color: #2c3e50;
}

.order-date {
    margin: 0;
    color: #7f8c8d;
    font-size: 0.9rem;
}

.order-total {
    font-size: 1.2rem;
    font-weight: bold;
    color: #2c3e50;
}

.order-status {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-bottom: 1rem;
    font-weight: 500;
}

.order-items {
    margin-bottom: 1.5rem;
}

.order-item {
    display: flex;
    justify-content: space-between;
    padding: 0.5rem 0;
    border-bottom: 1px solid #e1e8ed;
}

.order-item:last-child {
    border-bottom: none;
}

.item-name {
    color: #2c3e50;
}

.item-price {
    color: #7f8c8d;
    font-weight: 500;
}

.order-actions {
    display: flex;
    gap: 1rem;
}

.action-btn.outline {
    background: transparent;
    border: 1px solid #3498db;
    color: #3498db;
    padding: 0.5rem 1rem;
    border-radius: 5px;
    cursor: pointer;
    transition: all 0.3s ease;
}

.action-btn.outline:hover {
    background: #3498db;
    color: white;
}

.action-btn.primary {
    background: #3498db;
    border: 1px solid #3498db;
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 5px;
    cursor: pointer;
    transition: background 0.3s ease;
}

.action-btn.primary:hover {
    background: #2980b9;
}

/* Empty States */
.empty-state {
    text-align: center;
    padding: 3rem 2rem;
    color: #7f8c8d;
}

.empty-state svg {
    margin-bottom: 1rem;
    opacity: 0.5;
}

.empty-state h3,
.empty-state h4 {
    margin: 0 0 1rem 0;
    color: #2c3e50;
}

.empty-state p {
    margin: 0 0 2rem 0;
    max-width: 400px;
    margin-left: auto;
    margin-right: auto;
}

.not-logged-in {
    text-align: center;
    padding: 4rem 2rem;
    background: white;
    border-radius: 15px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

/* Form Actions */
.form-actions {
    display: flex;
    gap: 1rem;
    margin-top: 1rem;
}

.auth-button.secondary {
    background: #95a5a6;
    color: white;
}

.auth-button.secondary:hover {
    background: #7f8c8d;
}

.auth-success {
    background: #d4edda;
    color: #155724;
    padding: 0.75rem 1rem;
    border-radius: 8px;
    border: 1px solid #c3e6cb;
    font-size: 0.9rem;
}

/* Checkbox Group */
.checkbox-group {
    margin-top: 1rem;
}

.checkbox-label {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    cursor: pointer;
    font-weight: normal;
    margin: 0;
}

.checkbox-label input[type="checkbox"] {
    width: 18px;
    height: 18px;
}

/* Responsive Profile */
@media (max-width: 768px) {
    .profile-header {
        flex-direction: column;
        text-align: center;
        gap: 1.5rem;
    }

    .edit-profile-btn {
        position: static;
        align-self: center;
    }

    .profile-stats {
        justify-content: center;
    }

    .profile-tabs {
        flex-wrap: wrap;
    }

    .tab-btn {
        flex: 1 0 calc(50% - 0.5rem);
        min-width: 140px;
    }

    .section-header {
        flex-direction: column;
        gap: 1rem;
        align-items: flex-start;
    }

    .addresses-grid {
        grid-template-columns: 1fr;
    }

    .order-header {
        flex-direction: column;
        gap: 1rem;
    }

    .order-actions {
        flex-direction: column;
    }

    .form-actions {
        flex-direction: column;
    }
}

@media (max-width: 480px) {
    .profile-page {
        padding: 1rem 0;
    }

    .profile-header,
    .tab-content {
        padding: 1.5rem;
    }

    .tab-btn {
        flex: 1 0 100%;
    }
}

/* Router Link Styles */
a {
    text-decoration: none;
    color: inherit;
}

.product-category-link {
    display: inline-block;
}

.category {
    color: #7f8c8d;
    font-size: 0.9rem;
    text-transform: uppercase;
    margin-bottom: 0.5rem;
    transition: color 0.3s ease;
}

.product-category-link:hover .category {
    color: #3498db;
}

/* Not Found Page */
.not-found {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 60vh;
    text-align: center;
    padding: 2rem;
}

.not-found h1 {
    font-size: 3rem;
    margin-bottom: 1rem;
    color: #2c3e50;
}

.not-found p {
    font-size: 1.2rem;
    color: #7f8c8d;
    margin-bottom: 2rem;
}

/* Active Navigation State */
nav ul li a.active {
    background: #3498db;
    color: white;
}

/* CTA Button as Link */
.cta-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    text-decoration: none;
}

.view-all-btn {
    display: inline-flex;
    align-items: center;
    text-decoration: none;
}

/* User Action as Link */
.user-action-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    width: 100%;
    padding: 0.75rem 1rem;
    background: none;
    border: none;
    text-align: left;
    cursor: pointer;
    transition: background 0.3s ease;
    font-size: 0.9rem;
    color: #2c3e50;
    text-decoration: none;
}

.user-action-btn:hover {
    background: #f8f9fa;
}

